import './Table.css';
import axios from 'axios';

function Table(props) {

  const handleDelete = (memoId) => {
    axios.delete(`/api/${memoId}`)
      .then(res => {
        if (res.data.message === "success") {
          props.getData(); // Refresh data after deletion
        }
      })
      .catch(err => console.log(err));
  };

  return (
    <div>
      <div className='heading'>
      <h1>Memo</h1>
      <button onClick={handleDeleteSelected} disabled={!selectedMemos.length} className="button-85" role="button">Delete Selected</button>
      <button onClick={handleDeleteAll} className="button-85" role="button">Delete All</button>
      
      <form class="example" action="action_page.php">
          <input type="text" placeholder="Search.." name="search"></input>
          <button type="submit" className="button-85" role="button"><i class="fa fa-search">Search</i></button>   
      </form>
      </div>
      <table>
        <thead>
          <tr>
            <td className="date">Date</td>
            <td className="text">Notes</td>
            <td className="date">Delete</td>
          </tr>
        </thead>
        <tbody>
          {props.memos.map(memo => (
            <tr key={memo._id}>
              <td className="date">{/* Add the date if available */}Date</td>
              <td className="text">{memo.text}</td>
              <td>
                <div className='btn'>
                  <button onClick={() => handleDelete(memo._id)} className="button-85" role="button">Delete</button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
