import './Table.css';
import axios from 'axios';
import { useState } from "react";

function Table(props) {
  
  const [selectedMemos, setSelectedMemos] = useState([]);

  const handleDelete = (memoId) => {
    axios.delete(`/api/${memoId}`)
      .then(res => {
        if (res.data.message === "success") {
          props.getData(); // Refresh data after deletion
        }
      })
      .catch(err => console.log(err));
  };


  const handleDeleteSelected = () => {
    axios.all(selectedMemos.map(id => axios.delete(`/api/${id}`)))
      .then(axios.spread((...responses) => {
        props.getData();
        setSelectedMemos([]);
      }))
      .catch(err => console.error("Error deleting selected memos:", err));
  };

  const handleDeleteAll = () => {
    axios.delete(`/api/`)
      .then(res => {
        if (res.data.message === "success") {
          props.getData();
        }
      })
      .catch(err => console.error("Error deleting all memos:", err));
  };

  const handleSelectMemo = (memoId) => {
    setSelectedMemos(prev => {
      if (prev.includes(memoId)) {
        return prev.filter(id => id !== memoId);
      } else {
        return [...prev, memoId];
      }
    });
  };

  return (
    <div>
      <div className='heading'>
      <h1>Memo</h1>
      <div>
      <button onClick={handleDeleteSelected} disabled={!selectedMemos.length} className="button-85" role="button" >Delete Selected</button>
      <button onClick={handleDeleteAll} className="button-85" role="button">Delete All</button>
      </div>
      <form class="example" action="action_page.php">
          <input type="text" placeholder="Search.." name="search"></input>
          <button type="submit" className="button-85" role="button"><i class="fa fa-search">Search</i></button>   
      </form>
      </div>
      <table>
        <thead>
          <tr>
            <td className="date">
            <input
                type="checkbox"
                onChange={(e) => {
                  if (e.target.checked) {
                    setSelectedMemos(props.memos.map(memo => memo._id));
                  } else {
                    setSelectedMemos([]);
                  }
                }}
                checked={selectedMemos.length === props.memos.length}
              />
            </td>
            <td className="text">Notes</td>
            <td className="date">Delete</td>
          </tr>
        </thead>
        <tbody>
          {props.memos.map(memo => (
            <tr key={memo._id}>
              <td className="date">
              <input
                  type="checkbox"
                  checked={selectedMemos.includes(memo._id)}
                  onChange={() => handleSelectMemo(memo._id)}
                />
              </td>
              <td className="text">{memo.text}</td>
              <td>
                <div className='btn'>
                  <button onClick={() => handleDelete(memo._id)} className="button-85" role="button">Delete</button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
