import './HomePage.css';
import Table from './Table';
import { useEffect, useState } from "react";
import axios from 'axios'


function Hemlo(props){
  return(<p>hello nhi {props.namaste}</p>);
}

function HomePage() {
  
  const [text, setText] = useState("")

  const [memos, setMemos] = useState([])
// doubt
  const getData = () => {
    axios.get('/api/')
    .then(res => {
        setMemos(res.data)
    })
    .catch(err=> console.log(err))
  }

  const handleAdd = (e) => {
    e.preventDefault()
    axios.post('/api/', {"text": text})
    .then(res => {
      if (res.data.message === "success"){
        getData()
        setText("")
      }
    })
    .catch(err=> console.log(err))
  }
useEffect(_ => getData(), [])


  return (
    <>
    <Hemlo namaste="hemlo"/>
    <div className="homepage">
        <Table memos={memos} getData={getData}/>
       <div className="text-area">
        <textarea value={text} onChange={e => setText(e.target.value)}></textarea>
        <button onClick={handleAdd} class="button-85" role="button">ADD</button>
       </div>
    </div>
    </>
  );
}

export default HomePage;
