import './HomePage.css';
import Table from './Table';
import { useEffect, useState } from "react";
import axios from 'axios';
import Pagination from '@mui/material/Pagination';
import Stack from '@mui/material/Stack';

function HomePage() {
  const [text, setText] = useState("");
  const [memos, setMemos] = useState([]);

  const getData = () => {
    axios.get('/api/')
      .then(res => {
        setMemos(res.data);
      })
      .catch(err => console.log(err));
  };

  const handleAdd = (e) => {
    e.preventDefault();
    const newMemo = { text: text };
    axios.post('/api/', newMemo)
      .then(res => {
        if (res.data.message === "success") {
          getData();
          setText("");
        }
      })
      .catch(err => console.log(err));
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="homepage">
      <Table memos={memos} getData={getData} />

      <Stack spacing={2}>
      <Pagination count={10} variant="outlined" shape="rounded" className='page' />
      </Stack>

      <div className="text-area">
        <textarea value={text} onChange={e => setText(e.target.value)}></textarea>
        <button onClick={handleAdd} className="button-85" role="button">ADD</button>
      </div>
    </div>
  );
}

export default HomePage;
