import './Table.css';
import axios from 'axios';

function Table(props) {

  const handleDelete = (memo) => {
    console.log(`Sending delete request for memo with ID: ${memo.id}`); // Logging for debugging
    axios.delete(`/api/${memo.id}`)
      .then(res => {
        if (res.data.message === "success") {
          props.getData(); // Ensure props.getData is called correctly
        }
      })
      .catch(err => console.log(err));
  };

  return (
    <div>
      <h1>Memo</h1>
      <table>
        <thead>
          <tr>
            <td className="date">Date</td>
            <td className="text">Notes</td>
            <td className="date">Delete</td>
          </tr>
        </thead>
        <tbody>
          {props.memos.map(memo => (
            <tr key={memo.id}>
              <td className="date">{/* Add the date if available */}</td>
              <td className="text">{memo.text}</td>
              <td>
                <div className='btn'>
                  <button onClick={() => handleDelete(memo)} className="button-85" role="button">Delete</button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
