import './HomePage.css';
import Table from './Table';
import { useEffect, useState } from "react";
import axios from 'axios';

function HomePage() {
  const [text, setText] = useState("");
  const [memos, setMemos] = useState([]);

  const getData = () => {
    axios.get('/api/')
      .then(res => {
        setMemos(res.data);
      })
      .catch(err => console.log(err));
  };

  const generateID = () => {
    return `memo-${new Date().getTime()}`;
  };

  const handleAdd = (e) => {
    e.preventDefault();
    const newMemo = { id: generateID(), text: text };
    axios.post('/api/', newMemo)
      .then(res => {
        if (res.data.message === "success") {
          getData();
          setText("");
        }
      })
      .catch(err => console.log(err));
  };

  useEffect(() => {
    getData();
  }, []);

    const handleDelete = (memo) => {
      axios.delete(`/api/${memo.id}`) // Ensure the memo ID is included in the URL
        .then(res => {
          if (res.data.message === "success") {
            props.getData(); // Ensure props.getData is called correctly
          }
        })
        .catch(err => console.log(err));
    };
    
  return (
    <div className="homepage">
      <Table memos={memos} getData={getData} />
      <div className="text-area">
        <textarea value={text} onChange={e => setText(e.target.value)}></textarea>
        <button onClick={handleAdd}>ADD</button>
      </div>
    </div>
  );
}

export default HomePage;
