import './Table.css';
import axios from 'axios';

function Table(props) {

  const handleDelete = (memoId) => {
    axios.delete(`/api/${memoId}`)
      .then(res => {
        if (res.data.message === "success") {
          props.getData(); // Refresh data after deletion
        }
      })
      .catch(err => console.error("Error deleting memo:", err));
  };

  // Function to format date and time
  const formatDateTime = (dateTimeString) => {
    const options = {
      year: "numeric", month: "short", day: "numeric",
      hour: "2-digit", minute: "2-digit", second: "2-digit",
      hour12: false
    };
    return new Date(dateTimeString).toLocaleDateString('en-US', options);
  };

  return (
    <div>
      <h1>Memo</h1>
      <table>
        <thead>
          <tr>
            <th className="date">Date</th>
            <th className="text">Notes</th>
            <th className="date">Delete</th>
          </tr>
        </thead>
        <tbody>
          {props.memos.map(memo => (
            <tr key={memo._id}>
              <td className="date">{formatDateTime(memo.createdAt)}</td>
              <td className="text">{memo.text}</td>
              <td>
                <div className='btn'>
                  <button onClick={() => handleDelete(memo._id)} className="button-85" role="button">Delete</button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
